// add classes on hover/touch
function initCustomHover() {
	jQuery('.three-columns .column').touchHover();
	jQuery('.three-cols .col a').touchHover();
	jQuery('.block-section .block').touchHover();
}


function initFooterBar() {
	var win = jQuery(window);
	var winHeight = win.height();
	var fixedClass = 'fixed';
	var animSpeed = 450;
	var hideClass = 'hide-fixed';
	var hiddenClass = 'hidden';
	var noAnimationClass = 'no-css-animation';
	var visualHeight, visualPosition;

	jQuery('#footer').each(function() {
		var holder = jQuery(this);
		var stickyElement = holder.find('.sticky-block');
		var visual = jQuery(stickyElement.data('visual'));
		var holderPosition = holder.offset().top;
		var stickyElementHeight = stickyElement.outerHeight();
		var timer;
		holder.addClass(noAnimationClass);

		function getVisualOptions() {
			if (!visual.length) {
				visualHeight = winHeight;
				visualPosition = 0;
			} else {
				visualHeight = visual.height();
				visualPosition = visual.offset().top;
			}
		}

		getVisualOptions();

		function setState() {
			var winScrollTop = win.scrollTop();

			if(winScrollTop + winHeight >= visualHeight + visualPosition && (winScrollTop + winHeight - (visualHeight + visualPosition)) < stickyElementHeight) {
				stickyElement.css({
					transform: 'translate(0,' + (stickyElementHeight - (winScrollTop + winHeight - (visualHeight + visualPosition)) + 5) + 'px)'
				});
			} else {
				stickyElement.css({
					transform: ''
				});
			}

			if(winScrollTop + winHeight <= visualHeight + visualPosition) {
				stickyElement.addClass(hiddenClass);
			} else {
				stickyElement.removeClass(hiddenClass);
			}

			if(winScrollTop + winHeight > visualHeight + visualPosition && winScrollTop + winHeight < holderPosition + stickyElementHeight) {
				stickyElement.addClass(fixedClass);
				holder.css({
					paddingTop: stickyElementHeight
				});
			} else {
				holder.css({
					paddingTop: ''
				});
				stickyElement.removeClass(fixedClass);
				if (visualPosition && winScrollTop + winHeight < holderPosition) {
					stickyElement.addClass(hideClass);
					if(timer) {
						clearTimeout(timer);
					}

					timer = setTimeout(function() {
						stickyElement.removeClass(hideClass);
					}, animSpeed);
				}
				
			}
		}
		setState();

		setTimeout(function() {
			holder.removeClass(noAnimationClass);
		}, 50);

		win.on('resize', function() {
			getVisualOptions();
			holderPosition = holder.offset().top;
			stickyElementHeight = stickyElement.outerHeight();
			winHeight = win.height();
			setState();
		});

		win.on('scroll', setState);
	});
}

function initMainVisualHover(){
	var hoverLeftClass = 'hover-left';
	var hoverRightClass = 'hover-right';
	var win = jQuery(window);
	var hiddenClass = 'js-hidden';
	var videoHoverClass = 'video-hover';

	jQuery('.promo-block').each(function() {
		var holder = jQuery(this);
		var cols = holder.find('.col');
		var videos = holder.find('video');
		var holderWidth = holder.width();
		var holderHeight = holder.height();
		var videoContainers = holder.find('.video-container');

		win.on('resize', function() {
			holderWidth = holder.width();
			holderHeight = holder.height();
		});

		cols.each(function(i) {
			var col = jQuery(this);
			var otherCol = cols.not(col);
			var videoContainer = holder.find(col.data('video'));
			var video = videoContainer.find('video');

			videoContainer.addClass(hiddenClass);

			col.on('mouseenter', function() {
				if(i > 0) {
					otherCol.addClass(hoverRightClass);
				} else {
					otherCol.addClass(hoverLeftClass);
				}
				videoContainer.removeClass(hiddenClass);
				video[0].play();
				holder.addClass(videoHoverClass);
			}).on('mouseleave', function() {
				otherCol.removeClass(hoverRightClass).removeClass(hoverLeftClass);
				video[0].pause();
				videoContainer.addClass(hiddenClass);
				holder.removeClass(videoHoverClass);
			});
		});

		videos.each(function(i) {
			var videoObj = jQuery(this);
			var video = this;
			var ratio = videoObj.width() / videoObj.height();
			videoObj.on('loadstart, loadeddata', function() {
				setVideoSize();
			});
			video.volume = 0;
			video.controls = false;
			win.on('resize', function() {
				setVideoSize();
			});

			function setVideoSize() {
				if(holderHeight > holderWidth / ratio) {
					var difference = holderHeight - holderWidth / ratio;
					videoObj.css({
						width: holderHeight * ratio,
						height: holderHeight,
						marginLeft: (holderWidth - holderHeight * ratio) / 2,
						marginTop:''
					});
				} else {
					videoObj.css({
						width: holderWidth,
						height: holderWidth / ratio,
						marginTop: (holderHeight - holderWidth / ratio) / 2,
						marginLeft:''
					});
				}
			}
			setVideoSize();
		});

		videoContainers.each(function() {
			var videoContainer = jQuery(this);
		});
	});
}

// fade gallery init
function initSlideShow() {
	jQuery('.slideshow').fadeGallery({
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		generatePagination: '.pagination',
		event: 'click',
		useSwipe: true,
		autoRotation: true,
		autoHeight: true,
		switchTime: 7000,
		animSpeed: 500
	});
}

// content tabs init
function initTabs() {
	jQuery('ul.tabset').tabset({
		tabLinks: 'a',
		addToParent: true,
		defaultTab: false
	});
}

// accordion menu init
function initAccordion() {
	jQuery('#nav ul').slideAccordion({
		opener: 'li.menu-item-has-children a',
		slider: '.sub-menu',
		animSpeed: 300
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('body').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'nav-active',
		menuOpener: '.nav-opener',
		menuDrop: '.nav-drop'
	});
}

// align blocks height
function initSameHeight() {
	jQuery('.about-block').sameHeight({
		elements: '.article',
		flexible: true,
		multiLine: true
	});
	jQuery('.same-height-parent').sameHeight({
		elements: '.same-height-block',
		flexible: true,
		multiLine: true
	});

	jQuery('.same-height').sameHeight({
		elements: '.height',
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});
}

function initRetinaCover() {
	jQuery('.bg-stretch').retinaCover();
}

