function initLayout(){
	jQuery('.w1, .w2').each(function(){
		var crawLine = jQuery(this);
		
		ResponsiveHelper.addRange({
		    '..767': { 
		        on: function() {
					var instCrawLine = crawLine.data('Marquee');
					
					if(instCrawLine) {
						setTimeout(function(){
							instCrawLine.destroy();
						},100);
					}
		        }
		    },
			'768..': {
				on: function() {
					setTimeout(function(){
						crawLine.marquee({
							line: '.thubs-list',
							animSpeed: 50,
							direction: 'right',
						});
					},100);
		        }
			}
		});
	})
}

